import React, { useState } from 'react';
import { FaGoogle, FaEnvelope, FaLock, FaSignInAlt } from 'react-icons/fa';
import './Auth.css';

const Login = ({ onLogin, switchToRegister }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    
    try {
      // Local login API call would go here
      const response = await fetch('http://localhost:8000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Giriş başarısız');
      }
      
      const data = await response.json();
      // Store token in localStorage
      localStorage.setItem('token', data.token);
      // Add token to user object before passing it to the callback
      const userWithToken = {
        ...data.user,
        token: data.token
      };
      onLogin(userWithToken);
    } catch (err) {
      setError(err.message || 'Giriş yaparken bir hata oluştu');
      console.error('Login error:', err);
      
      // For demo purposes - allow login without backend
      if (email && password) {
        console.log('Demo login activated');
        // Generate a dummy token for demo purposes
        const demoToken = `demo_${Math.random().toString(36).substring(2)}`;
        localStorage.setItem('token', demoToken);
        const demoUser = { 
          id: 1, 
          email, 
          name: email.split('@')[0],
          token: demoToken 
        };
        localStorage.setItem('demoUser', JSON.stringify(demoUser));
        onLogin(demoUser);
      }
    }
  };

  const handleGoogleLogin = async () => {
    // In a real implementation, redirect to Google OAuth endpoint
    try {
      window.location.href = 'http://localhost:8000/api/auth/google/login';
    } catch (err) {
      setError('Google ile giriş yapılırken bir hata oluştu');
      console.error('Google login error:', err);
      
      // For demo purposes
      const demoUser = { id: 2, email: 'google.user@gmail.com', name: 'Google User' };
      localStorage.setItem('demoUser', JSON.stringify(demoUser));
      onLogin(demoUser);
    }
  };

  return (
    <div className="auth-container">
      <h2 className="auth-title">Giriş Yap</h2>
      
      {error && <div className="auth-error">{error}</div>}
      
      <form onSubmit={handleSubmit} className="auth-form">
        <div className="form-group">
          <label>
            <FaEnvelope className="icon" />
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="E-posta"
              required
            />
          </label>
        </div>
        
        <div className="form-group">
          <label>
            <FaLock className="icon" />
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Şifre"
              required
            />
          </label>
        </div>
        
        <button type="submit" className="auth-button">
          <FaSignInAlt className="icon" />
          Giriş Yap
        </button>
        
        <div className="divider">veya</div>
        
        <button 
          type="button" 
          className="google-button"
          onClick={handleGoogleLogin}
        >
          <FaGoogle className="icon" />
          Google ile Giriş Yap
        </button>
      </form>
      
      <p className="auth-switch">
        Hesabınız yok mu?{' '}
        <span onClick={switchToRegister}>Kayıt Ol</span>
      </p>
    </div>
  );
};

export default Login;
