import React, { useState, useEffect } from 'react';
import './App.css';
import ChatArea from './components/ChatArea';
import Sidebar from './components/Sidebar';
import ModelSelector from './components/ModelSelector';
import AuthPage from './components/Auth/AuthPage';
import UserProfile from './components/UserProfile';

function App() {
  const [theme, setTheme] = useState('light');
  const [messages, setMessages] = useState([]);
  const [selectedModel, setSelectedModel] = useState(null);
  const [showModelSelector, setShowModelSelector] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    document.body.className = `${theme}-theme`;
  }, [theme]);

  useEffect(() => {
    // Check if user is already logged in (from localStorage)
    const storedUser = localStorage.getItem('demoUser');
    if (storedUser) {
      try {
        setUser(JSON.parse(storedUser));
      } catch (e) {
        console.error('Error parsing stored user:', e);
      }
    }
  }, []);

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  const handleAuthSuccess = (userData) => {
    setUser(userData);
    // After authentication, show model selector
    setShowModelSelector(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('demoUser');
    setUser(null);
    setMessages([]);
    setSelectedModel(null);
    setShowModelSelector(false);
  };

  const handleSendMessage = async (messageText) => {
    if (!messageText.trim()) return;

    // Add user message to the chat
    const userMessage = {
      id: Date.now(),
      text: messageText,
      sender: 'user',
      timestamp: new Date().toISOString()
    };

    setMessages(prevMessages => [...prevMessages, userMessage]);

    try {
      // API endpoint based on selected model
      const endpoint = selectedModel === 'api' 
        ? 'http://localhost:8000/api/chat' 
        : 'http://localhost:8000/api/custom-model';
      
      // Include auth token if available
      const headers = {
        'Content-Type': 'application/json',
      };
      
      const token = localStorage.getItem('token');
      if (token) {
        headers['Authorization'] = `Bearer ${token}`;
      }
      
      const response = await fetch(endpoint, {
        method: 'POST',
        headers,
        body: JSON.stringify({ message: messageText }),
      });

      if (!response.ok) {
        throw new Error('API response was not ok');
      }

      const data = await response.json();
      
      // Add bot response to chat
      const botMessage = {
        id: Date.now() + 1,
        text: data.response,
        sender: 'bot',
        timestamp: new Date().toISOString()
      };

      setMessages(prevMessages => [...prevMessages, botMessage]);
    } catch (error) {
      console.error('Error:', error);
      
      // Add error message
      const errorMessage = {
        id: Date.now() + 1,
        text: 'Yanıt verilemiyor, lütfen tekrar deneyin.',
        sender: 'bot',
        isError: true,
        timestamp: new Date().toISOString()
      };

      setMessages(prevMessages => [...prevMessages, errorMessage]);
    }
  };

  const clearChat = () => {
    setMessages([]);
  };

  const selectModel = (model) => {
    setSelectedModel(model);
    setShowModelSelector(false);
  };

  const resetModelSelection = () => {
    setSelectedModel(null);
    setShowModelSelector(true);
    setMessages([]);
  };

  // If not logged in, show auth page
  if (!user) {
    return <AuthPage onAuthSuccess={handleAuthSuccess} />;
  }

  return (
    <div className="app">
      {showModelSelector ? (
        <ModelSelector onSelectModel={selectModel} />
      ) : selectedModel ? (
        <>
          <Sidebar
            toggleTheme={toggleTheme}
            theme={theme}
            clearChat={clearChat}
            resetModelSelection={resetModelSelection}
            selectedModel={selectedModel}
          >
            <UserProfile user={user} onLogout={handleLogout} />
          </Sidebar>
          <ChatArea
            messages={messages}
            onSendMessage={handleSendMessage}
            theme={theme}
          />
        </>
      ) : (
        <ModelSelector onSelectModel={selectModel} />
      )}
    </div>
  );
}

export default App;
