import React from 'react';
import { FaMoon, FaSun, FaTrash, FaChevronLeft, FaRobot, FaCalculator } from 'react-icons/fa';
import './Sidebar.css';

const Sidebar = ({ toggleTheme, theme, clearChat, resetModelSelection, selectedModel, children }) => {
  return (
    <div className="sidebar">
      {children}
      
      <div className="sidebar-header">
        <div className="model-info">
          {selectedModel === 'api' ? (
            <>
              <FaRobot className="model-icon-small" />
              <span>Hazır API Modeli</span>
            </>
          ) : (
            <>
              <FaCalculator className="model-icon-small" />
              <span>Özel Eğitilmiş Model</span>
            </>
          )}
        </div>
      </div>
      
      <div className="sidebar-buttons">
        <button 
          className="sidebar-button" 
          onClick={toggleTheme}
          title={theme === 'light' ? 'Karanlık Moda Geç' : 'Aydınlık Moda Geç'}
        >
          {theme === 'light' ? <FaMoon /> : <FaSun />}
        </button>
        
        <button 
          className="sidebar-button" 
          onClick={clearChat}
          title="Sohbeti Temizle"
        >
          <FaTrash />
        </button>
        
        <button 
          className="sidebar-button" 
          onClick={resetModelSelection}
          title="Model Seçimine Dön"
        >
          <FaChevronLeft />
        </button>
      </div>
    </div>
  );
};

export default Sidebar;
