import React from 'react';
import { useNavigate } from 'react-router-dom';
import { FaRobot, FaArrowRight } from 'react-icons/fa';
import './Layout.css';

const LandingPage = ({ isDarkMode }) => {
  const navigate = useNavigate();

  return (
    <div className={`landing-page ${isDarkMode ? 'dark' : 'light'}`}>
      <div className="hero-section">
        <div className="hero-icon">
          <FaRobot />
        </div>
        <h1>ChatGPT Clone</h1>
        <p className="hero-description">
          Yapay zeka destekli sohbet asistanınız ile tanışın.
          Sorularınızı sorun, cevapları alın.
        </p>
        <button 
          className="get-started-button"
          onClick={() => navigate('/register')}
        >
          Hemen Başla <FaArrowRight />
        </button>
        <div className="features">
          <div className="feature-card">
            <h3>Hızlı ve Kolay</h3>
            <p>Saniyeler içinde başlayın ve sohbete hemen katılın.</p>
          </div>
          <div className="feature-card">
            <h3>7/24 Kullanılabilir</h3>
            <p>İstediğiniz zaman, istediğiniz yerden erişin.</p>
          </div>
          <div className="feature-card">
            <h3>Güvenli ve Özel</h3>
            <p>Verileriniz güvende, sohbetleriniz özel.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
