import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import Header from './components/Layout/Header';
import Footer from './components/Layout/Footer';
import Sidebar from './components/Sidebar/Sidebar';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import UserProfile from './components/UserProfile/UserProfile';
import ChatArea from './components/ChatArea';
import LandingPage from './components/Layout/LandingPage';

function App() {
  const [user, setUser] = useState(null);
  const [chats, setChats] = useState([]);
  const [activeChat, setActiveChat] = useState(null);
  const [isDarkMode, setIsDarkMode] = useState(
    localStorage.getItem('darkMode') === 'true'
  );

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }

    // Eğer localStorage'da chat yok ise veya oturum açtıktan sonra yüklüyorsak API'den çekmeyi deneyelim
    const loadChats = async () => {
      try {
        if (localStorage.getItem('token')) {
          // API'den chat geçmişini yüklemeyi dene
          const response = await fetch('http://localhost:8000/api/chats', {
            headers: {
              'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
          });
          
          if (response.ok) {
            const chatData = await response.json();
            setChats(chatData);
            localStorage.setItem('chats', JSON.stringify(chatData));
            return;
          }
        }
        
        // API çağrısı başarısız olursa localStorage'dan yükle
        const storedChats = localStorage.getItem('chats');
        if (storedChats) {
          setChats(JSON.parse(storedChats));
        }
      } catch (err) {
        console.error('Sohbet geçmişi yüklenirken hata:', err);
        // Hata durumunda localStorage'dan yükle
        const storedChats = localStorage.getItem('chats');
        if (storedChats) {
          setChats(JSON.parse(storedChats));
        }
      }
    };
    
    loadChats();
  }, []);

  useEffect(() => {
    localStorage.setItem('darkMode', isDarkMode);
    document.body.classList.toggle('dark-mode', isDarkMode);
  }, [isDarkMode]);

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  const handleLogin = (userData) => {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
  };

  const handleLogout = () => {
    setUser(null);
    setChats([]);
    setActiveChat(null);
    localStorage.removeItem('user');
    localStorage.removeItem('chats');
    localStorage.removeItem('token');
  };

  const handleUpdateUser = (updatedUser) => {
    setUser(updatedUser);
    localStorage.setItem('user', JSON.stringify(updatedUser));
  };

  const handleNewChat = async () => {
    const newChat = {
      id: Date.now().toString(),
      title: 'Yeni Sohbet',
      messages: [],
      createdAt: new Date().toISOString()
    };

    try {
      const response = await fetch('http://localhost:8000/api/chats', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(newChat)
      });

      if (response.ok) {
        const savedChat = await response.json();
        if (!savedChat.messages) savedChat.messages = [];
        const updatedChats = [savedChat, ...chats];
        setChats(updatedChats);
        setActiveChat(savedChat);
        localStorage.setItem('chats', JSON.stringify(updatedChats));
      } else {
        // Fallback to local storage in demo mode
        const updatedChats = [newChat, ...chats];
        setChats(updatedChats);
        setActiveChat(newChat);
        localStorage.setItem('chats', JSON.stringify(updatedChats));
      }
    } catch (err) {
      console.error('Error creating new chat:', err);
      // Fallback to local storage in demo mode
      const updatedChats = [newChat, ...chats];
      setChats(updatedChats);
      setActiveChat(newChat);
      localStorage.setItem('chats', JSON.stringify(updatedChats));
    }
  };

  const handleDeleteChat = async (chatId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/chats/${chatId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const updatedChats = chats.filter(chat => chat.id !== chatId);
        setChats(updatedChats);
        if (activeChat?.id === chatId) {
          setActiveChat(null);
        }
        localStorage.setItem('chats', JSON.stringify(updatedChats));
      }
    } catch (err) {
      console.error('Error deleting chat:', err);
      // Fallback to local storage in demo mode
      const updatedChats = chats.filter(chat => chat.id !== chatId);
      setChats(updatedChats);
      if (activeChat?.id === chatId) {
        setActiveChat(null);
      }
      localStorage.setItem('chats', JSON.stringify(updatedChats));
    }
  };

  return (
    <Router>
      <div className={`app ${isDarkMode ? 'dark' : 'light'}`}>
        <Header 
          user={user} 
          onLogout={handleLogout} 
          isDarkMode={isDarkMode}
          toggleDarkMode={toggleDarkMode}
        />
        <main className="main-content">
          <Routes>
            <Route
              path="/login"
              element={
                user ? (
                  <Navigate to="/" />
                ) : (
                  <Login onLogin={handleLogin} isDarkMode={isDarkMode} />
                )
              }
            />
            <Route
              path="/register"
              element={
                user ? (
                  <Navigate to="/" />
                ) : (
                  <Register onLogin={handleLogin} isDarkMode={isDarkMode} />
                )
              }
            />
            <Route
              path="/profile"
              element={
                user ? (
                  <UserProfile
                    user={user}
                    onLogout={handleLogout}
                    onUpdateUser={handleUpdateUser}
                    isDarkMode={isDarkMode}
                  />
                ) : (
                  <Navigate to="/login" />
                )
              }
            />
            <Route
              path="/chat"
              element={
                user ? (
                  <div className="chat-layout">
                    <Sidebar
                      chats={chats}
                      activeChat={activeChat}
                      onChatSelect={setActiveChat}
                      onNewChat={handleNewChat}
                      onDeleteChat={handleDeleteChat}
                      isDarkMode={isDarkMode}
                      user={user}
                    />
                    <div className="chat-container">
                      {activeChat ? (
                        <ChatArea
                          messages={activeChat.messages || []}
                          onSendMessage={(message) => {
                            // Yeni mesaj gönderme mantığı
                            const newMessage = {
                              id: Date.now().toString(),
                              content: message,
                              sender: 'user',
                              timestamp: new Date().toISOString()
                            };
                            const updatedChat = {
                              ...activeChat,
                              messages: [...(activeChat.messages || []), newMessage]
                            };
                            const updatedChats = chats.map(chat => 
                              chat.id === activeChat.id ? updatedChat : chat
                            );
                            setChats(updatedChats);
                            setActiveChat(updatedChat);
                            localStorage.setItem('chats', JSON.stringify(updatedChats));
                          }}
                          theme={isDarkMode ? 'dark' : 'light'}
                          isLoading={false}
                        />
                      ) : (
                        <div className="welcome-message">
                          <h2>Hoş Geldiniz, {user.name}!</h2>
                          <p>Yeni bir sohbet başlatmak için sol menüden "Yeni Sohbet" butonuna tıklayın.</p>
                        </div>
                      )}
                    </div>
                  </div>
                ) : (
                  <Navigate to="/login" />
                )
              }
            />
            <Route
              path="/"
              element={
                user ? (
                  <Navigate to="/chat" />
                ) : (
                  <LandingPage isDarkMode={isDarkMode} />
                )
              }
            />
          </Routes>
        </main>
        <Footer isDarkMode={isDarkMode} />
      </div>
    </Router>
  );
}

export default App;
