import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './UserProfile.css';

const UserProfile = ({ user, onLogout, isDarkMode, onUpdateUser }) => {
  const navigate = useNavigate();
  const [isEditing, setIsEditing] = useState(false);
  const [name, setName] = useState(user?.name || '');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!user) {
      navigate('/login');
    }
  }, [user, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await fetch('/api/users/profile', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({ name })
      });

      const data = await response.json();

      if (response.ok) {
        onUpdateUser({ ...user, name });
        setIsEditing(false);
      } else {
        setError(data.message || 'Güncelleme başarısız');
      }
    } catch (err) {
      setError('Sunucu hatası. Lütfen daha sonra tekrar deneyin.');
      console.error('Profile update error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    onLogout();
    navigate('/login');
  };

  if (!user) return null;

  return (
    <div className={`profile-container ${isDarkMode ? 'dark' : 'light'}`}>
      <div className="profile-box">
        <h2>Profil Bilgileri</h2>
        {error && <div className="error-message">{error}</div>}
        
        <div className="profile-info">
          <div className="info-group">
            <label>Kullanıcı ID</label>
            <p>{user.id}</p>
          </div>
          
          <div className="info-group">
            <label>E-posta</label>
            <p>{user.email}</p>
          </div>
          
          <div className="info-group">
            {isEditing ? (
              <form onSubmit={handleSubmit}>
                <label htmlFor="name">İsim</label>
                <div className="edit-name-container">
                  <input
                    type="text"
                    id="name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                  />
                  <div className="edit-buttons">
                    <button 
                      type="submit" 
                      className="save-button"
                      disabled={loading}
                    >
                      {loading ? 'Kaydediliyor...' : 'Kaydet'}
                    </button>
                    <button 
                      type="button"
                      className="cancel-button"
                      onClick={() => {
                        setIsEditing(false);
                        setName(user.name);
                      }}
                    >
                      İptal
                    </button>
                  </div>
                </div>
              </form>
            ) : (
              <>
                <label>İsim</label>
                <div className="name-container">
                  <p>{user.name}</p>
                  <button 
                    className="edit-button"
                    onClick={() => setIsEditing(true)}
                  >
                    Düzenle
                  </button>
                </div>
              </>
            )}
          </div>
          
          <div className="info-group">
            <label>Katılma Tarihi</label>
            <p>{new Date(user.createdAt).toLocaleDateString('tr-TR')}</p>
          </div>
        </div>

        <div className="profile-actions">
          <button 
            className="logout-button"
            onClick={handleLogout}
          >
            Çıkış Yap
          </button>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
