import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Auth.css';

const Register = ({ onLogin, isDarkMode }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    if (formData.password !== formData.confirmPassword) {
      setError('Şifreler eşleşmiyor');
      setLoading(false);
      return;
    }

    try {
      const response = await fetch('http://localhost:8000/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          name: formData.name,
          email: formData.email,
          password: formData.password
        })
      });

      const data = await response.json();
      console.log("API yanıtı:", data);

      if (response.ok && data.success) {
        localStorage.setItem('token', data.token);
        const userData = {
          ...data.user,
          createdAt: new Date().toISOString(),
          database_connected: data.database_connected
        };
        localStorage.setItem('user', JSON.stringify(userData));
        localStorage.setItem('database_connected', data.database_connected);
        onLogin(userData);
        navigate('/');
      } else {
        setError(data.message || 'Kayıt başarısız');
        console.log("Sunucu hatası:", response.status, data);

        if (!data.success && data.demo_mode) {
          alert("Veritabanı bağlantısı kurulamadı: " + data.message + "\n\nDemo moduna geçiliyor.");
          handleDemoRegister();
        }
      }
    } catch (err) {
      console.error('Register error:', err);
      alert("Sunucu bağlantı hatası. Demo moduna geçiliyor.");
      handleDemoRegister();
    } finally {
      setLoading(false);
    }
  };

  const handleDemoRegister = () => {
    try {
      const demoUser = {
        id: 'demo-user-' + Math.random().toString(36).substring(7),
        name: formData.name || 'Demo Kullanıcı',
        email: 'demo@example.com',
        token: 'demo_token_' + Math.random().toString(36).substring(7),
        createdAt: new Date().toISOString()
      };
      
      localStorage.setItem('token', demoUser.token);
      localStorage.setItem('user', JSON.stringify(demoUser));
      localStorage.setItem('demoMode', 'true');
      
      onLogin(demoUser);
      navigate('/');
    } catch (error) {
      console.error('Demo register error:', error);
      setError('Demo modunda bir hata oluştu. Lütfen tekrar deneyin.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={`auth-container ${isDarkMode ? 'dark' : 'light'}`}>
      <div className="auth-box">
        <h2>Kayıt Ol</h2>
        {error && <div className="error-message">{error}</div>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">İsim</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">E-posta</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Şifre</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="confirmPassword">Şifre Tekrar</label>
            <input
              type="password"
              id="confirmPassword"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              required
            />
          </div>
          <button 
            type="submit" 
            className="submit-button"
            disabled={loading}
          >
            {loading ? 'Kayıt yapılıyor...' : 'Kayıt Ol'}
          </button>
        </form>
        <div className="auth-links">
          <button 
            onClick={handleDemoRegister}
            className="demo-button"
            disabled={loading}
          >
            Demo Modunda Dene
          </button>
          <p>
            Zaten hesabınız var mı?{' '}
            <span onClick={() => navigate('/login')}>
              Giriş Yap
            </span>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Register;
