import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import Header from './components/Layout/Header';
import Footer from './components/Layout/Footer';
import Sidebar from './components/Sidebar/Sidebar';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import UserProfile from './components/UserProfile/UserProfile';
import ChatArea from './components/ChatArea';
import LandingPage from './components/Layout/LandingPage';

function App() {
  const [user, setUser] = useState(null);
  const [chats, setChats] = useState([]);
  const [activeChat, setActiveChat] = useState(null);
  const [isDarkMode, setIsDarkMode] = useState(
    localStorage.getItem('darkMode') === 'true'
  );

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }

    const storedChats = localStorage.getItem('chats');
    if (storedChats) {
      setChats(JSON.parse(storedChats));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('darkMode', isDarkMode);
    document.body.classList.toggle('dark-mode', isDarkMode);
  }, [isDarkMode]);

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  const handleLogin = (userData) => {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
  };

  const handleLogout = () => {
    setUser(null);
    setChats([]);
    setActiveChat(null);
    localStorage.removeItem('user');
    localStorage.removeItem('chats');
    localStorage.removeItem('token');
  };

  const handleUpdateUser = (updatedUser) => {
    setUser(updatedUser);
    localStorage.setItem('user', JSON.stringify(updatedUser));
  };

  const handleNewChat = async () => {
    const newChat = {
      id: Date.now().toString(),
      title: 'Yeni Sohbet',
      messages: [],
      createdAt: new Date().toISOString()
    };

    try {
      const response = await fetch('/api/chats', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(newChat)
      });

      if (response.ok) {
        const savedChat = await response.json();
        setChats([savedChat, ...chats]);
        setActiveChat(savedChat);
        localStorage.setItem('chats', JSON.stringify([savedChat, ...chats]));
      }
    } catch (err) {
      console.error('Error creating new chat:', err);
      // Fallback to local storage in demo mode
      setChats([newChat, ...chats]);
      setActiveChat(newChat);
      localStorage.setItem('chats', JSON.stringify([newChat, ...chats]));
    }
  };

  const handleDeleteChat = async (chatId) => {
    try {
      const response = await fetch(`/api/chats/${chatId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const updatedChats = chats.filter(chat => chat.id !== chatId);
        setChats(updatedChats);
        if (activeChat?.id === chatId) {
          setActiveChat(null);
        }
        localStorage.setItem('chats', JSON.stringify(updatedChats));
      }
    } catch (err) {
      console.error('Error deleting chat:', err);
      // Fallback to local storage in demo mode
      const updatedChats = chats.filter(chat => chat.id !== chatId);
      setChats(updatedChats);
      if (activeChat?.id === chatId) {
        setActiveChat(null);
      }
      localStorage.setItem('chats', JSON.stringify(updatedChats));
    }
  };

  return (
    <Router>
      <div className={`app ${isDarkMode ? 'dark' : 'light'}`}>
        <Header 
          user={user} 
          onLogout={handleLogout} 
          isDarkMode={isDarkMode}
          toggleDarkMode={toggleDarkMode}
        />
        <main className="main-content">
          <Routes>
            <Route
              path="/login"
              element={
                user ? (
                  <Navigate to="/" />
                ) : (
                  <Login onLogin={handleLogin} isDarkMode={isDarkMode} />
                )
              }
            />
            <Route
              path="/register"
              element={
                user ? (
                  <Navigate to="/" />
                ) : (
                  <Register onLogin={handleLogin} isDarkMode={isDarkMode} />
                )
              }
            />
            <Route
              path="/profile"
              element={
                user ? (
                  <UserProfile
                    user={user}
                    onLogout={handleLogout}
                    onUpdateUser={handleUpdateUser}
                    isDarkMode={isDarkMode}
                  />
                ) : (
                  <Navigate to="/login" />
                )
              }
            />
            <Route
              path="/chat"
              element={
                user ? (
                  <div className="chat-layout">
                    <Sidebar
                      chats={chats}
                      activeChat={activeChat}
                      onChatSelect={setActiveChat}
                      onNewChat={handleNewChat}
                      onDeleteChat={handleDeleteChat}
                      isDarkMode={isDarkMode}
                      user={user}
                    />
                    <div className="chat-container">
                      {activeChat ? (
                        <ChatArea
                          chat={activeChat}
                          isDarkMode={isDarkMode}
                          user={user}
                        />
                      ) : (
                        <div className="welcome-message">
                          <h2>Hoş Geldiniz, {user.name}!</h2>
                          <p>Yeni bir sohbet başlatmak için sol menüden "Yeni Sohbet" butonuna tıklayın.</p>
                        </div>
                      )}
                    </div>
                  </div>
                ) : (
                  <Navigate to="/login" />
                )
              }
            />
            <Route
              path="/"
              element={
                user ? (
                  <Navigate to="/chat" />
                ) : (
                  <LandingPage isDarkMode={isDarkMode} />
                )
              }
            />
          </Routes>
        </main>
        <Footer isDarkMode={isDarkMode} />
      </div>
    </Router>
  );
}

export default App;
