import React, { useState, useEffect } from 'react';
import './App.css';
import ChatArea from './components/ChatArea';
import Sidebar from './components/Sidebar';
import ModelSelector from './components/ModelSelector';
import AuthPage from './components/Auth/AuthPage';
import UserProfile from './components/UserProfile';

function App() {
  const [theme, setTheme] = useState('light');
  const [messages, setMessages] = useState([]);
  const [selectedModel, setSelectedModel] = useState(null);
  const [showModelSelector, setShowModelSelector] = useState(false);
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  // API anahtarları (normalde bu istemci tarafında saklanmamalıdır - sadece demo amaçlıdır)
  // const OPENAI_API_KEY = "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";
  const WOLFRAM_API_KEY = "7278E5-JW82YPX5LK";
  const HUGGINGFACE_API_KEY = "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";
  const HUGGINGFACE_API_URL = "https://api-inference.huggingface.co/models/luffycodes/MathWordProblemSolver";

  useEffect(() => {
    document.body.className = `${theme}-theme`;
  }, [theme]);

  useEffect(() => {
    // Check if user is already logged in (from localStorage)
    const storedUser = localStorage.getItem('demoUser');
    if (storedUser) {
      try {
        setUser(JSON.parse(storedUser));
      } catch (e) {
        console.error('Error parsing stored user:', e);
      }
    }
  }, []);

  useEffect(() => {
    // Fetch chat history when user selects a model
    if (user && selectedModel) {
      fetchChatHistory(selectedModel);
    }
  }, [user, selectedModel]);

  const fetchChatHistory = async (model) => {
    if (!user) return;
    
    setIsLoading(true);
    
    try {
      // Önce backend API ile deneyelim
      const token = localStorage.getItem('token');
      const headers = {
        'Content-Type': 'application/json',
      };
      
      if (token) {
        headers['Authorization'] = `Bearer ${token}`;
      }
      
      const modelType = model === 'api' ? 'api' : 'custom';
      const response = await fetch(`http://localhost:8000/api/chat-history?model=${modelType}`, {
        headers,
      });
      
      if (!response.ok) {
        throw new Error('Failed to fetch chat history');
      }
      
      const data = await response.json();
      setMessages(data.messages || []);
    } catch (error) {
      console.error('Error fetching chat history:', error);
      // Backend çalışmıyorsa localStorage'dan yükle
      const modelKey = selectedModel === 'api' ? 'api' : 'custom';
      const storedMessages = localStorage.getItem(`${modelKey}_messages`);
      if (storedMessages) {
        try {
          setMessages(JSON.parse(storedMessages));
        } catch (e) {
          console.error('Error parsing stored messages:', e);
        }
      }
    } finally {
      setIsLoading(false);
    }
  };

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  const handleAuthSuccess = (userData) => {
    setUser(userData);
    // After authentication, show model selector
    setShowModelSelector(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('demoUser');
    
    // Tüm modellerin mesajlarını temizle
    localStorage.removeItem('api_messages');
    localStorage.removeItem('custom_messages');
    
    setUser(null);
    setMessages([]);
    setSelectedModel(null);
    setShowModelSelector(false);
  };

  // Doğrudan OpenAI API'ye istek gönderen yardımcı fonksiyon - kota hatası nedeniyle kullanmıyoruz
  /*
  const callOpenAI = async (message) => {
    const response = await fetch('https://api.openai.com/v1/chat/completions', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${OPENAI_API_KEY}`
      },
      body: JSON.stringify({
        model: 'gpt-3.5-turbo',
        messages: [{ role: 'user', content: message }],
        temperature: 0.7
      })
    });

    if (!response.ok) {
      const error = await response.json();
      throw new Error(error.error?.message || 'OpenAI API hatası');
    }

    const data = await response.json();
    return data.choices[0].message.content;
  };
  */

  // Demo chatbot - API olmadan demo yanıtlar üreten fonksiyon
  const demoChatbot = (message) => {
    const lowerMsg = message.toLowerCase();
    
    if (lowerMsg.includes('merhaba') || lowerMsg.includes('selam')) {
      return 'Merhaba! Size nasıl yardımcı olabilirim?';
    }
    
    if (lowerMsg.includes('nasılsın')) {
      return 'Teşekkür ederim, ben bir AI asistanım. Duygularım yok ama size yardımcı olmaktan memnuniyet duyarım!';
    }
    
    if (lowerMsg.includes('hava') && (lowerMsg.includes('nasıl') || lowerMsg.includes('durumu'))) {
      return 'Üzgünüm, gerçek zamanlı hava durumu verilerine erişimim yok. Ancak güvenilir bir hava durumu uygulaması veya sitesi kullanmanızı öneririm.';
    }
    
    if (lowerMsg.includes('yemek') || lowerMsg.includes('tarif')) {
      return 'Yemek tarifleri konusunda size yardımcı olabilirim. Ne tür bir yemek yapmak istiyorsunuz?';
    }
    
    if (lowerMsg.includes('film') || lowerMsg.includes('öneri')) {
      return 'Film önerisi istiyorsanız, son zamanlarda çıkan popüler filmler: "Dune", "No Time to Die", "The French Dispatch". Ne tür filmlerden hoşlanırsınız?';
    }
    
    if (lowerMsg.includes('kitap')) {
      return 'Kitap önerileri için önce hangi türleri sevdiğinizi bilmem gerekiyor. Roman, bilim kurgu, fantastik, tarih veya kişisel gelişim kitapları hakkında konuşabiliriz.';
    }
    
    if (lowerMsg.includes('kod') || lowerMsg.includes('programlama') || lowerMsg.includes('yazılım')) {
      return 'Programlama hakkında sorularınız varsa, hangi dil veya framework ile ilgilendiğinizi belirtirseniz daha spesifik yardım sağlayabilirim.';
    }
    
    if (lowerMsg.includes('teşekkür')) {
      return 'Rica ederim! Başka bir sorunuz olursa yardımcı olmaktan memnuniyet duyarım.';
    }
    
    if (lowerMsg.length < 5) {
      return 'Daha ayrıntılı bir şey sorar mısınız? Size daha iyi yardımcı olabilirim.';
    }
    
    // Genel yanıt
    return `"${message}" hakkında yardımcı olmak isterim. Lütfen sorularınızı detaylandırırsanız daha iyi cevaplar verebilirim.`;
  };

  // Wolfram Alpha API'sini kullanarak matematiksel soruları çözme
  const callWolframAlpha = async (query) => {
    try {
      // CORS engelleri nedeniyle proxy üzerinden istek gönderiyoruz
      const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
      const wolframUrl = `https://api.wolframalpha.com/v1/result?appid=${WOLFRAM_API_KEY}&i=${encodeURIComponent(query)}`;
      
      const response = await fetch(proxyUrl + wolframUrl);
      
      if (!response.ok) {
        // Eğer Wolfram API yanıt vermezse, basit matematik modelini kullan
        return simpleMathModel(query);
      }
      
      const result = await response.text();
      return result;
    } catch (error) {
      console.error('Wolfram Alpha API error:', error);
      // Hata durumunda basit matematik modelini kullan
      return simpleMathModel(query);
    }
  };

  // Demo matematik modeli (basit işlemler için)
  const simpleMathModel = (message) => {
    try {
      // Basit matematik işlemleri için
      if (/[0-9+\-*/()^. ]/.test(message) && /[+\-*/^]/.test(message)) {
        try {
          // eslint-disable-next-line no-eval
          const result = eval(message.replace('^', '**'));
          return `${message} = ${result}`;
        } catch (e) {
          return `Matematik sorunuzu anlayamadım. Lütfen başka bir şekilde sorun.`;
        }
      }
      
      const lowerMsg = message.toLowerCase();
      
      if (lowerMsg.includes('toplama') || lowerMsg.includes('topla')) {
        return 'Toplama işlemi için doğrudan sayıları yazabilirsiniz. Örnek: 5 + 3';
      }
      
      if (lowerMsg.includes('çıkarma') || lowerMsg.includes('çıkar')) {
        return 'Çıkarma işlemi için doğrudan sayıları yazabilirsiniz. Örnek: 10 - 4';
      }
      
      if (lowerMsg.includes('çarpma') || lowerMsg.includes('çarp')) {
        return 'Çarpma işlemi için doğrudan sayıları yazabilirsiniz. Örnek: 6 * 7';
      }
      
      if (lowerMsg.includes('bölme') || lowerMsg.includes('böl')) {
        return 'Bölme işlemi için doğrudan sayıları yazabilirsiniz. Örnek: 20 / 5';
      }
      
      if (lowerMsg.includes('kare') || lowerMsg.includes('üssü')) {
        return 'Üs alma işlemi için doğrudan sayıları yazabilirsiniz. Örnek: 2^3 veya 5^2';
      }
      
      if (lowerMsg.includes('türev') || lowerMsg.includes('integral') || 
          lowerMsg.includes('limit') || lowerMsg.includes('denklem')) {
        return `Bu tür karmaşık matematik soruları için doğrudan sorunuzu yazabilirsiniz. Örneğin: "türev x^2" veya "integral sin(x)".`;
      }
      
      return `Matematik sorunuz: "${message}" anlaşılamadı. Lütfen doğrudan matematik işlemini yazın. Örnek: 5 + 3, 10 - 2, 4 * 7, 20 / 5, 2^3, türev x^2, integral sin(x)`;
    } catch (e) {
      return `Bir hata oluştu: ${e.message}`;
    }
  };

  // Matematiksel sorgunun türünü belirle
  const isMathQuery = (query) => {
    const mathKeywords = ['türev', 'integral', 'limit', 'denklem', 'çöz', 'hesapla', 
                          'derivative', 'integrate', 'solve', 'equation', 'calculate', 
                          'kök', 'faktör', 'simplify', 'expand'];
    
    const mathRegex = /[0-9+\-*/()^.=<>√∫∂∑π]/;
    
    const lowerQuery = query.toLowerCase();
    
    // Matematik anahtar kelimesi içeriyor mu?
    const hasMathKeyword = mathKeywords.some(keyword => lowerQuery.includes(keyword));
    
    // Matematik sembolü veya sayı içeriyor mu?
    const hasMathSymbol = mathRegex.test(query);
    
    return hasMathKeyword || hasMathSymbol;
  };

  // Hugging Face API'sini kullanarak matematiksel soruları çözme
  const callHuggingFaceAPI = async (query) => {
    try {
      // Backend proxy endpointini kullan (CORS sorunlarını önlemek için)
      const response = await fetch('http://localhost:8000/api/huggingface/math-solver', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({ message: query })
      });
      
      if (!response.ok) {
        throw new Error('Hugging Face API yanıt vermedi');
      }
      
      const result = await response.json();
      return result.response;
    } catch (error) {
      console.error('Hugging Face API error:', error);
      
      // Backend çalışmıyorsa doğrudan Hugging Face API'sine istek gönder
      try {
        const directResponse = await fetch(HUGGINGFACE_API_URL, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${HUGGINGFACE_API_KEY}`
          },
          body: JSON.stringify({ inputs: query })
        });
        
        if (!directResponse.ok) {
          // Doğrudan istek de başarısız olursa Wolfram Alpha'yı dene
          return callWolframAlpha(query);
        }
        
        const directResult = await directResponse.json();
        
        // API yanıtının formatını kontrol et
        if (Array.isArray(directResult) && directResult.length > 0) {
          return directResult[0].generated_text || JSON.stringify(directResult);
        } else if (directResult.generated_text) {
          return directResult.generated_text;
        } else {
          return JSON.stringify(directResult);
        }
      } catch (directError) {
        console.error('Direct Hugging Face API error:', directError);
        // Her iki yöntem de başarısız olursa Wolfram Alpha'yı dene
        return callWolframAlpha(query);
      }
    }
  };

  const handleSendMessage = async (messageText) => {
    if (!messageText.trim()) return;

    // Add user message to the chat
    const userMessage = {
      id: Date.now(),
      text: messageText,
      sender: 'user',
      timestamp: new Date().toISOString()
    };

    setMessages(prevMessages => [...prevMessages, userMessage]);

    try {
      let responseText;

      // Gelen mesajın matematik sorusu olup olmadığını kontrol et
      if (selectedModel === 'custom') {
        // Matematik sorularını önce Hugging Face API ile dene
        responseText = await callHuggingFaceAPI(messageText);
      } else if (selectedModel === 'api' && isMathQuery(messageText)) {
        // API modunda matematik sorgusu ise önce Hugging Face, sonra Wolfram'ı dene
        responseText = await callHuggingFaceAPI(messageText);
      } else {
        // Sohbet soruları için demo chatbot kullan
        responseText = demoChatbot(messageText);
      }
      
      // Add bot response to chat
      const botMessage = {
        id: Date.now() + 1,
        text: responseText,
        sender: 'bot',
        timestamp: new Date().toISOString()
      };

      const updatedMessages = [...messages, userMessage, botMessage];
      setMessages(updatedMessages);
      
      // Save to local storage
      const modelKey = selectedModel === 'api' ? 'api' : 'custom';
      localStorage.setItem(`${modelKey}_messages`, JSON.stringify(updatedMessages));
    } catch (error) {
      console.error('Error:', error);
      
      // Add error message
      const errorMessage = {
        id: Date.now() + 1,
        text: `Yanıt verilemiyor, lütfen tekrar deneyin. Hata: ${error.message}`,
        sender: 'bot',
        isError: true,
        timestamp: new Date().toISOString()
      };

      const updatedMessages = [...messages, userMessage, errorMessage];
      setMessages(updatedMessages);
      localStorage.setItem(`${selectedModel}_messages`, JSON.stringify(updatedMessages));
    }
  };

  const clearChat = async () => {
    try {
      // Backend ile deneme
      if (user && selectedModel) {
        const token = localStorage.getItem('token');
        const headers = {
          'Content-Type': 'application/json',
        };
        
        if (token) {
          headers['Authorization'] = `Bearer ${token}`;
        }
        
        const modelType = selectedModel === 'api' ? 'api' : 'custom';
        await fetch(`http://localhost:8000/api/chat-history?model=${modelType}`, {
          method: 'DELETE',
          headers,
        });
      }
    } catch (error) {
      console.error('Error clearing chat history from backend:', error);
    }
    
    // Clear local state and storage
    setMessages([]);
    const modelKey = selectedModel === 'api' ? 'api' : 'custom';
    localStorage.removeItem(`${modelKey}_messages`);
  };

  const selectModel = (model) => {
    setSelectedModel(model);
    setShowModelSelector(false);
  };

  const resetModelSelection = () => {
    setSelectedModel(null);
    setShowModelSelector(true);
    setMessages([]);
  };

  // If not logged in, show auth page
  if (!user) {
    return <AuthPage onAuthSuccess={handleAuthSuccess} />;
  }

  return (
    <div className="app">
      {showModelSelector ? (
        <ModelSelector onSelectModel={selectModel} />
      ) : selectedModel ? (
        <>
          <Sidebar
            toggleTheme={toggleTheme}
            theme={theme}
            clearChat={clearChat}
            resetModelSelection={resetModelSelection}
            selectedModel={selectedModel}
          >
            <UserProfile user={user} onLogout={handleLogout} />
          </Sidebar>
          <ChatArea
            messages={messages}
            onSendMessage={handleSendMessage}
            theme={theme}
            isLoading={isLoading}
          />
        </>
      ) : (
        <ModelSelector onSelectModel={selectModel} />
      )}
    </div>
  );
}

export default App;
